//modulo de usuario
module.exports = (sequelize,DataTypes) => {
    let ausuarios = sequelize.define('usuarios',{
        usu_tipo : DataTypes.STRING
    });

    ausuarios.associate = function (models) {
        ausuarios.hasMany(models.profesor,{ //un usuario puede tener muchos profesores o administradores
            foreignKey : 'prof_id_usu',
            as: 'profesor' //clave for√°nea con profesor
        });
    };

    ausuarios.associate = function (models) {
        ausuarios.hasMany(models.administrador,{ 
            foreignKey : 'adm_id_usu',
            as: 'administrador' 
        });
    };

    return ausuarios;
}