//modulo de administrador
module.exports = (sequelize,DataTypes) => {
    let administrador = sequelize.define('administrador',{
        adm_nombre : DataTypes.STRING,
        adm_apellidos: DataTypes.STRING,
        adm_codigo: DataTypes.STRING,
        adm_password: DataTypes.STRING,
        adm_correo: DataTypes.STRING,
        adm_telefono: DataTypes.STRING
    });

    administrador.associate = function (models) {
        administrador.belongsTo(models.usuarios,{
            onDelete : "CASCADE",
            foreignKey : 'adm_id_usu' 
        });
    };

    return administrador;
}