//index.js
const userController = require('../controller').usuario;

module.exports = (app) => {

    app.get('/api',(req,res) => {
        res.status(200).send({
            data : "Welcome Node Sequlize API v1"
        })
    })

    app.get('/api/usuarios',userController.getAllUsers);
    app.post('/api/usuarios/crear',userController.create);
    app.put('/api/usuarios/actualizar', userController.update);
}